package es.iessaladillo.esthercastaneda.mastermind.ui.menu.homeimport android.os.Bundleimport androidx.fragment.app.Fragmentimport androidx.navigation.NavControllerimport androidx.navigation.fragment.NavHostFragmentimport es.iessaladillo.esthercastaneda.mastermind.Rimport kotlinx.android.synthetic.main.home_fragment.*import kotlinx.android.synthetic.main.main_activity.*class HomeFragment : Fragment(R.layout.home_fragment) {    private val navController: NavController by lazy {        NavHostFragment.findNavController(navHostFragment)    }    override fun onActivityCreated(savedInstanceState: Bundle?) {        super.onActivityCreated(savedInstanceState)        setupViews()    }    private fun setupViews() {        setupButtons()    }    private fun setupButtons() {        btnPlay.setOnClickListener {            navController.navigate(R.id.difficultGameSelect)        }        btnManual.setOnClickListener {            navController.navigate(R.id.manualFragment)        }        btnConfig.setOnClickListener {            navController.navigate(R.id.settingsFragment)        }    }}