package es.iessaladillo.esthercastaneda.mastermind.ui.menu.homeimport android.os.Bundleimport android.view.Viewimport android.view.animation.AlphaAnimationimport android.view.animation.Animationimport androidx.fragment.app.Fragmentimport androidx.navigation.NavControllerimport androidx.navigation.fragment.NavHostFragmentimport es.iessaladillo.esthercastaneda.mastermind.Rimport kotlinx.android.synthetic.main.home_fragment.*import kotlinx.android.synthetic.main.main_activity.*class HomeFragment : Fragment(R.layout.home_fragment) {    private val navController: NavController by lazy {        NavHostFragment.findNavController(navHostFragment)    }    override fun onActivityCreated(savedInstanceState: Bundle?) {        super.onActivityCreated(savedInstanceState)        setupViews()    }    private fun setupViews() {        btnPlay.blink()        setupButtons()    }    private fun View.blink(times: Int = Animation.INFINITE, duration: Long = 1000L, offset: Long = 20L, minAlpha: Float = 0.1f, maxAlpha: Float = 1.0f, repeatMode: Int = Animation.REVERSE ) {        startAnimation(AlphaAnimation(minAlpha, maxAlpha).also {            it.duration = duration            it.startOffset = offset            it.repeatMode = repeatMode            it.repeatCount = times        })    }    private fun setupButtons() {        btnPlay.setOnClickListener {            navController.navigate(R.id.action_homeFragment_to_modeGame)        }        btnInstructions.setOnClickListener {            navController.navigate(R.id.action_homeFragment_to_manualFragment)        }        btnSet.setOnClickListener {            navController.navigate(R.id.action_homeFragment_to_settingsFragment)        }    }}